<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>看拼音写词 - 完整专业版 V17</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <script src="https://unpkg.com/pinyin-pro"></script>
    <style>
        @font-face { font-family: 'Kaiti'; src: local('STKaiti'), local('KaiTi'), local('楷体'); }
        body { background-color: #f8fafc; margin: 0; }
        @media print {
            .no-print { display: none !important; }
            body, html { background: white !important; height: auto !important; overflow: visible !important; margin: 0 !important; padding: 0 !important; }
            .a4-page {
                box-shadow: none !important;
                margin: 0 !important;
                border: none !important;
                padding: 10mm 10mm 40mm 10mm !important; 
                display: block !important;
                width: 210mm !important;
                height: 296.5mm !important; 
                position: relative !important;
                page-break-inside: avoid !important;
                break-inside: avoid !important;
            }
            .a4-page + .a4-page { page-break-before: always !important; break-before: page !important; }
        }
        .a4-page { width: 210mm; min-height: 297mm; padding: 15mm 15mm 50mm 15mm; margin: 20px auto; background: white; box-shadow: 0 4px 20px rgba(0,0,0,0.08); position: relative; box-sizing: border-box; display: block; }
        .score-group { display: flex; border: 1px solid #000; width: fit-content; }
        .score-cell { width: 14px; height: 14px; border-right: 1px solid #000; display: flex; align-items: center; justify-content: center; font-size: 10px; font-weight: bold; }
        .score-cell:last-child { border-right: none; }
        .score-cell.idx { background-color: #f3f4f6; width: 18px; font-size: 9px; }
        .pinyin-container { width: 130px; height: 32px; display: flex; align-items: flex-end; overflow: visible; padding-bottom: 2px; }
        .pinyin-text { font-family: 'Kaiti', 'STKaiti', serif; font-weight: bold; line-height: 1; white-space: nowrap; display: inline-block; transform-origin: left bottom; font-size: 26px; }

        .fixed-answer-footer {
            position: absolute;
            bottom: 10mm;
            left: 10mm;
            right: 10mm;
            height: 35mm;
            display: flex;
            flex-direction: column;
            justify-content: flex-end;
        }

        .scope-label { font-size: 10px; color: #64748b; text-align: left; font-weight: bold; margin-bottom: 4px; }
        .divider-line { border-top: 1.5px dashed #000; width: 100%; margin-bottom: 10px; }

        /* 答案区：改为正向显示 */
        .answer-normal-view { transform: none; }

        .kaiti { font-family: 'Kaiti', 'STKaiti', serif; }
        #sidebar { min-width: 300px; max-width: 800px; position: relative; }
        #resizer { width: 6px; cursor: col-resize; background-color: transparent; transition: background-color 0.2s; position: relative; z-index: 50; }
        #resizer:hover, #resizer.resizing { background-color: #3b82f6; }
        .setting-row { display: flex; align-items: flex-start; padding: 10px 0; border-bottom: 1px solid #f1f5f9; }
        .col-cb { width: 30px; flex-shrink: 0; }
        .col-unit { width: 85px; flex-shrink: 0; font-weight: bold; font-size: 12px; color: #64748b; }
        .col-words { flex: 1; font-size: 13px; color: #334155; line-height: 1.6; }
        .fixed-bottom-bar { position: absolute; bottom: 0; left: 0; right: 0; background: white; padding: 20px; border-top: 1px solid #f1f5f9; }
    </style>
</head>
<body class="flex flex-row h-screen overflow-hidden">
    <div id="sidebar" class="no-print h-full flex flex-col bg-white shadow-sm z-20">
        <header class="p-6 border-b shrink-0"><h1 class="text-xl font-black text-slate-800 italic">看拼音写词 <span class="text-blue-600">V17</span></h1></header>
        <div class="flex-1 overflow-y-auto px-6 pb-28" id="unitListContainer"></div>
        <div class="fixed-bottom-bar no-print">
            <button onclick="window.print()" class="w-full bg-slate-900 text-white py-4 rounded-xl font-bold active:scale-95 transition shadow-lg text-lg hover:bg-black">预览并打印听写卷</button>
        </div>
    </div>
    <div id="resizer" class="no-print h-full"></div>
    <div id="preview-scroll-area" class="flex-1 h-full overflow-y-auto p-8 bg-slate-100 flex flex-col items-center">
        <div id="pagesContainer" class="space-y-8 pb-20"></div>
    </div>
    <script>
        const { pinyin } = pinyinPro;
        const charToWordMap = {"球":"打球", "呼":"招呼", "读":"读书", "笛":"笛子", "罚":"处罚", "互":"互相", "碰":"碰撞", "黄":"黄色", "急":"急忙", "庭":"庭院", "相":"相信", "未":"未来", "寒":"寒冷", "径":"小径", "斜":"倾斜", "枫":"枫叶", "霜":"严霜", "挑":"挑选", "深":"深沉", "落":"落下", "珠":"珍珠", "粘":"粘贴", "印":"印象", "案":"方案", "展":"展现", "凉":"凉快", "杏":"杏花", "枚":"一枚", "爽":"清爽", "挤":"拥挤", "争":"争取", "菊":"菊花", "频":"频度", "勾":"勾勒", "挖":"挖土", "油":"油画", "等":"等待", "钻":"钻孔", "爬":"爬行", "漂":"漂亮", "晒":"晾晒", "葫":"葫芦", "芦":"芦苇", "错":"错误", "普":"普通", "宫":"宫殿", "肯":"肯定", "冒":"冒险", "式":"形式", "怜":"怜悯", "旅":"旅行", "另":"另外", "晴":"晴天", "及":"及时", "卷":"试卷", "齿":"牙齿", "胃":"肠胃", "管":"管理", "刚":"刚才", "咬":"咬断", "申":"申请", "介":"介绍", "绍":"介绍", "宗":"祖宗", "旨":"宗旨", "占":"占据", "乏":"缺乏", "搭":"搭建", "亲":"亲人", "祖":"祖国", "披":"披上", "摇":"摇动", "停":"停止", "羽":"羽毛", "翠":"翠绿", "蓝":"蓝色", "吞":"吞咽", "蒲":"蒲公英", "英":"英雄", "耍":"玩耍", "使":"使用", "劲":"使劲", "脸":"脸庞", "欠":"欠钱", "朝":"朝向", "钓":"钓鱼", "察":"观察", "拢":"合拢", "喜":"喜欢", "景":"景象", "优":"优秀", "淡":"平淡", "浅":"深浅", "底":"底部", "岩":"岩石", "鹿":"梅花鹿", "划":"划分", "布":"棉布", "茂":"茂密", "密":"秘密", "厚":"宽厚", "料":"料理", "滨":"海滨", "棕":"棕色", "帆":"帆船", "灰":"灰色", "跟":"跟着", "渔":"渔民", "壳":"贝壳", "院":"院子", "亚":"亚军", "透":"透明", "除":"除去", "踩":"踩踏", "抽":"抽象", "封":"封闭", "严":"严厉", "挡":"阻挡", "坛":"花坛", "显":"显示", "苍":"苍白", "药":"药品", "材":"材料", "软":"柔软", "刮":"刮风", "捉":"捕捉", "返":"返回", "望":"希望", "断":"中断", "楚":"清楚", "至":"至少", "岸":"岸边", "孤":"孤独", "饮":"饮料", "亦":"亦可", "欲":"欲望", "抹":"抹去", "宜":"便宜", "妙":"奇妙", "奏":"演奏", "琴":"钢琴", "柔":"柔和", "感":"感谢", "充":"充足", "威":"威严", "器":"武器", "汇":"汇集", "鸣":"鸣叫", "塘":"池塘", "虾":"小虾", "昆":"昆虫", "仅":"仅仅", "序":"顺序", "荣":"荣誉", "枯":"枯萎", "姿":"姿势", "态":"态度", "刺":"刺激", "梨":"梨树", "部":"部分", "奥":"奥秘", "秘":"秘密", "螺":"田螺", "螃":"螃蟹", "蟹":"螃蟹", "鲤":"鲤鱼", "鲫":"鲫鱼", "鲨":"鲨鱼", "司":"司机", "登":"登山", "跌":"跌倒", "皆":"皆知", "弃":"放弃", "持":"持久", "击":"打击", "念":"念书", "差":"差别", "考":"考试", "试":"试题", "均":"平均", "退":"后退", "努":"努力", "单":"单位", "留":"留心", "度":"态度", "奋":"奋斗", "棒":"木棒", "伤":"伤害", "陆":"陆地", "血":"血液", "取":"取消", "盘":"盘子", "匆":"匆忙", "医":"医生", "迅":"迅速", "速":"速度", "夺":"夺取... [truncated]
        const dataOrdered = [
            { title: "单元1", vocab: ["山坡", "学校", "飘扬", "课文", "声音", "招引", "热闹", "古老", "粗壮", "枝干", "洁白"], chars: "球招呼读" },
            { title: "单元2", vocab: ["轰响", "阵雨", "湿润", "风笛", "狂欢", "觉得", "功课", "放学", "老师", "急急忙忙"], chars: "笛罚互碰黄" },
            { title: "单元4", vocab: [], chars: "庭相未寒径斜枫霜挑深" },
            { title: "单元5", vocab: ["秋风", "放晴", "明朗", "地面", "亮晶晶", "落叶", "图案", "闪闪发光", "尽头", "排列", "规则", "凌乱", "歌唱", "迟到"], chars: "珠粘印案展" },
            { title: "单元6", vocab: ["秋天", "清凉", "炎热", "枫树", "邮票", "凉爽", "果树", "菊花", "仙子", "频频", "气味", "香甜", "松果", "丰收"], chars: "凉杏枚爽挤争菊勾挖油" },
            { title: "单元8", vocab: ["门板", "准备", "旁边", "暴风雨", "安心", "低头", "吃力", "再见", "母鸡", "注意", "屋子", "漂亮", "意思", "因此"], chars: "等哦钻爬漂晒" },
            { title: "语文园地1", vocab: ["恒心", "神圣", "萌发", "妥当", "车轴", "阁楼", "培植", "厘米"], chars: "" },
            { title: "单元11", vocab: ["声明", "神仙", "普通", "让步", "条件", "指甲", "得到", "衣服", "所以", "要是", "同学", "可怜", "最好", "科学"], chars: "葫芦错普宫肯冒式" },
            { title: "单元12", vocab: ["旅行", "要好", "答应", "做梦", "来得及", "救命", "大吃一惊", "尾巴", "牙齿", "肚皮", "食物", "消化", "当然", "刚才", "知觉", "光亮"], chars: "另晴及卷齿胃管刚咬" },
            { title: "语文园地2", vocab: ["申请", "介绍", "主旨", "占领", "乏力"], chars: "" },
            { title: "单元14", vocab: ["母亲", "外祖父", "船夫", "羽毛", "翠绿", "静悄悄", "翠鸟", "捕鱼"], chars: "搭亲祖披摇停蓝吞" },
            { title: "单元15", vocab: ["草地", "蒲公英", "盛开", "玩耍", "一本正经", "使劲", "钓鱼", "观察", "合拢", "张开", "喜爱"], chars: "蒲英耍脸欠" },
            { title: "单元16", vocab: ["风景", "优美", "物产", "交错", "岩石", "鹿角", "成群结队", "布满", "条纹", "周身", "皮球", "茂密", "肥料", "祖国", "事业", "发展"], chars: "淡浅底划布厚料" },
            { title: "单元17", vocab: ["海滨", "街道", "交界", "水平线", "机帆船", "来来往往", "朝阳", "渔民", "贝壳", "汽笛", "出海", "银光闪闪", "庭院", "亚热带", "散发", "打扫", "干净"], chars: "滨棕帆灰跟渔壳院亚透除踩" },
            { title: "单元18", vocab: ["东北", "密密层层", "严严实实", "视线", "山谷", "起来", "照射", "各种各样", "花坛", "显得", "苍翠", "药材", "捕捉", "野兔", "景色", "宝库"], chars: "抽封严挡坛显苍软刮捉" },
            { title: "单元20", vocab: [], chars: "返望断楚至岸孤饮亦欲抹宜" },
            { title: "单元21", vocab: ["大自然", "美妙", "音乐家", "手风琴", "歌手", "感受", "温柔", "合奏", "充满", "威力", "乐器", "屋顶", "河流", "轻快", "合唱", "水塘"], chars: "妙奏琴柔感充威器汇鸣塘" },
            { title: "单元22", vocab: ["昆虫", "万物", "沉思", "搬家", "井然有序", "精神", "植物", "千姿百态", "鲜美", "池塘", "秋高气爽", "倒映", "游玩", "画册", "无穷", "奥秘", "无尽"], chars: "虾昆仅序荣枯姿态刺梨部" },
            { title: "语文园地3", vocab: ["田螺", "螃蟹", "鲤鱼", "鲫鱼", "鲨鱼"], chars: "螺螃蟹鲤鲫鲨" },
            { title: "单元23", vocab: [], chars: "司登跌皆弃持击" },
            { title: "单元24", vocab: ["生物", "从事", "成就", "学期", "考试", "再三", "同意", "难得", "值班", "努力", "留学", "国家", "落后", "地位", "环节", "难度", "刻苦", "兴奋"], chars: "念差均退努单留度奋" },
            { title: "单元25", vocab: ["手术台", "阵地", "战斗", "打响", "消灭", "伤员", "陆续", "血丝", "匆匆", "医生", "转告", "赶忙", "迅速", "争分夺秒", "连续"], chars: "棒伤陆血取盘匆医迅速夺秒" },
            { title: "语文园地4", vocab: ["怒目圆睁", "眨眼", "眼眶", "目瞪口呆", "耳闻目睹"], chars: "睁眨瞪瞅怒眶呆睹" }
        ];

        const processedBank = dataOrdered.map(item => {
            const allInUnit = [...item.vocab];
            const existStr = allInUnit.join("");
            [...item.chars].forEach(c => {
                if (!existStr.includes(c)) {
                    const phrase = charToWordMap[c] || c;
                    if (!allInUnit.includes(phrase)) allInUnit.push(phrase);
                }
            });
            return { title: item.title, words: allInUnit };
        });

        const sidebar = document.getElementById('sidebar');
        const resizer = document.getElementById('resizer');
        let isResizing = false;
        sidebar.style.width = localStorage.getItem('sidebarWidth') || '400px';
        resizer.addEventListener('mousedown', (e) => { isResizing = true; resizer.classList.add('resizing'); document.body.style.cursor = 'col-resize'; document.body.style.userSelect = 'none'; });
        document.addEventListener('mousemove', (e) => { if (!isResizing) return; const newWidth = e.clientX; if (newWidth >= 300 && newWidth <= 800) { sidebar.style.width = newWidth + 'px'; localStorage.setItem('sidebarWidth', newWidth); } });
        document.addEventListener('mouseup', () => { if (isResizing) { isResizing = false; resizer.classList.remove('resizing'); document.body.style.cursor = 'default'; document.body.style.userSelect = 'auto'; } });

        let selectedUnits = new Set(["单元1"]);

        function renderSettings() {
            document.getElementById('unitListContainer').innerHTML = processedBank.map((unitObj) => `
                <div class="setting-row cursor-pointer hover:bg-slate-50" onclick="toggleUnit('${unitObj.title}')">
                    <div class="col-cb"><input type="checkbox" ${selectedUnits.has(unitObj.title) ? 'checked' : ''} class="w-4 h-4 rounded-sm border-slate-300 pointer-events-none"></div>
                    <div class="col-unit">${unitObj.title}</div>
                    <div class="col-words">${unitObj.words.join('、')}</div>
                </div>`).join('');
            updatePreview();
        }
        function toggleUnit(title) { if (selectedUnits.has(title)) selectedUnits.delete(title); else selectedUnits.add(title); renderSettings(); }

        function updatePreview() {
            const container = document.getElementById('pagesContainer');
            container.innerHTML = '';
            let allSelected = [];
            processedBank.forEach(unitObj => { if (selectedUnits.has(unitObj.title)) { unitObj.words.forEach(word => allSelected.push({ word, unit: unitObj.title })); } });
            if (allSelected.length === 0) { container.innerHTML = '<div class="mt-40 text-slate-400 font-bold text-center">请勾选单元</div>'; return; }

            const PER_PAGE = 45; 
            for (let i = 0; i < allSelected.length; i += PER_PAGE) {
                const pageItems = allSelected.slice(i, i + PER_PAGE);
                const pageDiv = document.createElement('div');
                pageDiv.className = 'a4-page';
                const unitsOnPage = [...new Set(pageItems.map(item => item.unit))];
                const scopeText = "三上 - " + unitsOnPage.join('、');

                let gridHtml = '<div class="grid grid-cols-5 gap-y-10 gap-x-8">';
                pageItems.forEach((item, idx) => {
                    const globalIdx = i + idx + 1;
                    gridHtml += `
                        <div class="flex flex-col items-start gap-1">
                            <div class="pinyin-container"><span class="pinyin-text" id="py-${globalIdx}">${pinyin(item.word, {toneType:'symbol'})}</span></div>
                            <div class="score-group">
                                <div class="score-cell idx">${globalIdx}</div><div class="score-cell"></div><div class="score-cell"></div><div class="score-cell"></div><div class="score-cell"></div><div class="score-cell"></div><div class="score-cell"></div>
                            </div>
                        </div>`;
                });
                gridHtml += '</div>';

                let ansHtml = `
                    <div class="fixed-answer-footer">
                        <div class="scope-label">${scopeText}</div>
                        <div class="divider-line"></div>
                        <div class="answer-normal-view">
                            <div class="flex flex-wrap gap-x-4 gap-y-1 justify-start kaiti text-base leading-tight">
                                ${pageItems.map((item, idx) => `<span><small class="text-gray-400 text-[9px] mr-0.5">${i + idx + 1}</small>${item.word}</span>`).join('')}
                            </div>
                        </div>
                    </div>`;
                pageDiv.innerHTML = gridHtml + ansHtml;
                container.appendChild(pageDiv);
                setTimeout(() => { pageItems.forEach((_, idx) => { const el = document.getElementById(`py-${i+idx+1}`); if (el) { const pw=130; const sw=el.getBoundingClientRect().width; if(sw>pw) el.style.transform=`scale(${pw/sw})`; } }); }, 0);
            }
        }
        renderSettings();
    </script>
</body>
</html>

