    setLoading(true);
    fetch(`/data/${currentFile}`)
      .then(res => res.json())
      .then(data => {
        // 自动补全拼音
        const bank = data.wordBank.map(item => ({
          ...item,
          pinyin: item.pinyin || pinyin(item.word, { toneType: 'symbol' })
        }));
        setWordBank(bank);
        setLoading(false);
      })
      .catch(err => {
        console.error('加载词库失败:', err);
        setLoading(false);
      });
  }, [currentFile]);

  // 按单元分组
  const units = useMemo(() => {
    const groups = {};
    wordBank.forEach(word => {
      const unit = word.unit || '其他';
      if (!groups[unit]) groups[unit] = [];
      groups[unit].push(word);
    });
    return groups;
  }, [wordBank]);

  const toggleWord = (word) => {
    setSelectedWords(prev => 
      prev.find(w => w.word === word.word) 
        ? prev.filter(w => w.word !== word.word)
        : [...prev, word]
    );
  };

  const toggleUnit = (unit) => {
    const unitWords = units[unit];
    const allSelected = unitWords.every(uw => selectedWords.find(sw => sw.word === uw.word));

    if (allSelected) {
      setSelectedWords(prev => prev.filter(sw => !unitWords.find(uw => uw.word === sw.word)));
    } else {
      const newWords = unitWords.filter(uw => !selectedWords.find(sw => sw.word === uw.word));
      setSelectedWords(prev => [...prev, ...newWords]);
    }
  };

  const toggleExpand = (unit) => {
    setExpandedUnits(prev => ({ ...prev, [unit]: !prev[unit] }));
  };

  if (loading) return <div className="p-8 text-center">正在拼命加载词库...</div>;

  return (
    <div className="min-h-screen">
      {/* 操作界面 */}
      <div className="no-print p-4 max-w-4xl mx-auto pb-24">
        <header className="mb-8 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-blue-600">看拼音写词生成器</h1>
          <div className="flex gap-4">
            <select 
              className="border p-2 rounded shadow-sm bg-white"
              value={currentFile}
              onChange={(e) => {
                setCurrentFile(e.target.value);
                setSelectedWords([]);
              }}
            >
              {DATA_FILES.map(f => <option key={f} value={f}>{f.replace('.json', '')}</option>)}
            </select>
            <button 
              onClick={() => window.print()}
              disabled={selectedWords.length === 0}
              className="bg-blue-500 text-white px-4 py-2 rounded flex items-center gap-2 hover:bg-blue-600 disabled:opacity-50 transition-colors"
            >
              <Printer size={18} /> 打印模板 ({selectedWords.length})
            </button>
          </div>
        </header>

        <div className="bg-white rounded-xl shadow-lg overflow-hidden border border-gray-100">
          {Object.entries(units).map(([unit, unitWords]) => {
            const isExpanded = expandedUnits[unit];
            const selectedCount = unitWords.filter(uw => selectedWords.find(sw => sw.word === uw.word)).length;
            const isAllSelected = selectedCount === unitWords.length;

            return (
              <div key={unit} className="border-b last:border-0">
                <div className="flex items-center p-4 hover:bg-gray-50 transition-colors">
                  <button onClick={() => toggleExpand(unit)} className="mr-2">
                    {isExpanded ? <ChevronDown size={20} /> : <ChevronRight size={20} />}
                  </button>
                  <div className="flex-1 cursor-pointer" onClick={() => toggleExpand(unit)}>
                    <span className="font-bold">第 {unit} 单元</span>
                    <span className="ml-4 text-sm text-gray-500">已选 {selectedCount} / {unitWords.length}</span>
                  </div>
                  <button 
                    onClick={() => toggleUnit(unit)}
                    className={`flex items-center gap-1 px-3 py-1 rounded-full text-sm transition-all ${
                      isAllSelected ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                    }`}
                  >
                    <ListChecks size={14} /> {isAllSelected ? '取消全选' : '全选单元'}
                  </button>
                </div>

                {isExpanded && (
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2 p-4 bg-gray-50">
                    {unitWords.map(word => {
                      const isSelected = selectedWords.find(sw => sw.word === word.word);
                      return (
                        <div 
                          key={word.word}
                          onClick={() => toggleWord(word)}
                          className={`flex items-center p-2 rounded border cursor-pointer transition-all ${
                            isSelected ? 'border-blue-500 bg-blue-50' : 'border-gray-200 bg-white hover:border-blue-300'
                          }`}
                        >
                          <div className={`w-4 h-4 rounded-sm mr-2 flex items-center justify-center border ${
                            isSelected ? 'bg-blue-500 border-blue-500' : 'border-gray-300'
                          }`}>
                            {isSelected && <Check size={12} color="white" />}
                          </div>
                          <div>
                            <div className="text-sm font-medium">{word.word}</div>
                            <div className="text-[10px] text-gray-400">{word.pinyin}</div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* 打印区域 */}
      <div className="hidden print:block w-full">
        <h2 className="text-center text-xl font-bold mb-8">看拼音写词语练习</h2>

        {/* 题目列表 */}
        <div className="grid grid-cols-2 gap-y-12 gap-x-8">
          {selectedWords.map((word, index) => (
            <div key={index} className="flex flex-col gap-2">
              <div className="text-lg font-serif tracking-widest text-gray-700">
                {index + 1}. <span className="pinyin-text font-sans">{word.pinyin}</span>
              </div>
              <div className="flex gap-1">
                {Array(6).fill(0).map((_, i) => (
                  <div key={i} className="tian-ge"></div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* 底部答案区 - 旋转180度 */}
        <div className="mt-20 pt-8 border-t-2 border-dashed border-gray-300">
          <div className="upside-down">
            <div className="text-sm text-gray-400 mb-4 font-bold text-center">【正确答案（请倒过来看）】</div>
            <div className="flex flex-wrap gap-x-6 gap-y-2 justify-center">
              {selectedWords.map((word, index) => (
                <div key={index} className="text-base text-gray-600">
                  <span className="text-gray-400 text-xs mr-1">{index + 1}.</span>
                  {word.word}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

