import React, { useState, useEffect, useRef } from 'react';
import { Timer, X, Check, Lock, Unlock, HelpCircle } from 'lucide-react';

// --- MOCK DATA ---
const MOCK_WORDS = [
  { id: '1', word: '山坡', pinyin: 'shān pō', unit: '单元1', status: 'white' },
  { id: '2', word: '学校', pinyin: 'xué xiào', unit: '单元1', status: 'white' },
  { id: '3', word: '飘扬', pinyin: 'piāo yáng', unit: '单元1', status: 'red' }, // 模拟曾经错过的
  { id: '4', word: '课文', pinyin: 'kè wén', unit: '单元1', status: 'white' },
  { id: '5', word: '声音', pinyin: 'shēng yīn', unit: '单元1', status: 'white' },
];

export default function App() {
  const [mode, setMode] = useState('PRACTICE'); // PRACTICE, SELF_TEST, FINAL_TEST
  const [words, setWords] = useState(MOCK_WORDS);
  const [time, setTime] = useState(0);
  const [hintWord, setHintWord] = useState(null); // 长按显示的字

  // 计时逻辑
  useEffect(() => {
    const interval = setInterval(() => setTime(prev => prev + 1), 1000);
    return () => clearInterval(interval);
  }, []);

  const formatTime = (s) => {
    const mins = Math.floor(s / 60);
    const secs = s % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // 状态切换逻辑
  const toggleStatus = (id) => {
    setWords(prev => prev.map(w => {
      if (w.id === id) {
        // 练习模式下只有 白/红 切换
        return { ...w, status: w.status === 'red' ? 'white' : 'red' };
      }
      return w;
    }));
  };

  // 长按处理
  const handleHintStart = (wordItem) => {
    setHintWord(wordItem.word);
    // 自动标记为红色，但允许撤销
    setWords(prev => prev.map(w => 
      w.id === wordItem.id ? { ...w, status: 'red' } : w
    ));
  };

  return (
    <div className="flex flex-col h-screen overflow-hidden bg-slate-100 font-sans">
      {/* 顶部计时区 */}
      <header className="no-print bg-white/80 backdrop-blur-md border-b px-6 py-4 flex justify-between items-center z-50 shrink-0 shadow-sm">
        <div className="flex items-center gap-4">
          <h1 className="text-lg font-bold text-blue-600 tracking-tight">拼音听写 · 半自动版</h1>
          <div className="px-3 py-1 bg-slate-100 rounded-full text-xs font-medium text-slate-500">
            {mode === 'PRACTICE' ? '自由练习模式' : '严格自测中'}
          </div>
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-2 font-mono text-xl font-bold text-slate-700">
            <Timer size={20} className="text-blue-500" />
            {formatTime(time)}
          </div>
          <button className="bg-slate-900 text-white px-6 py-2 rounded-lg font-bold shadow-lg shadow-slate-200 active:scale-95 transition-all">
            结束并提交
          </button>
        </div>
      </header>

      {/* 主画布 - 模拟 A4 */}
      <main className="flex-1 overflow-y-auto p-4 md:p-8">
        <div className="a4-canvas p-10 md:p-16 rounded-sm min-h-[120vh]">
          <div className="grid grid-cols-2 gap-x-12 gap-y-16">
            {words.map((item, index) => (
              <div key={item.id} className="flex flex-col gap-4">
                {/* 拼音展示区 */}
                <div 
                  className="relative group cursor-help select-none"
                  onPointerDown={() => mode === 'PRACTICE' && handleHintStart(item)}
                  onPointerUp={() => setHintWord(null)}
                  onPointerLeave={() => setHintWord(null)}
                >
                  <span className="text-3xl font-bold font-kaiti tracking-widest text-slate-800">
                    {item.pinyin}
                  </span>

                  {/* 长按气泡 */}
                  {hintWord === item.word && (
                    <div className="absolute -top-16 left-0 bg-blue-600 text-white text-4xl px-6 py-3 rounded-2xl shadow-2xl shadow-blue-200 popover-up z-10 font-kaiti font-bold">
                      {item.word}
                      <div className="absolute -bottom-2 left-6 w-4 h-4 bg-blue-600 rotate-45"></div>
                    </div>
                  )}
                </div>

                {/* 方框操作区 */}
                <div className="flex gap-2 items-center">
                  {/* 序号格 */}
                  <div className="w-10 h-10 flex items-center justify-center bg-slate-50 border border-slate-200 rounded text-slate-400 text-sm font-bold">
                    {index + 1}
                  </div>

                  {/* 孩子标记格 */}
                  <button 
                    onClick={() => toggleStatus(item.id)}
                    className={`tian-ge flex items-center justify-center border-2 rounded-lg ${
                      item.status === 'red' 
                        ? 'border-red-500 bg-red-50 text-red-500' 
                        : 'border-slate-200 hover:border-blue-300'
                    }`}
                  >
                    {item.status === 'red' && <X size={28} strokeWidth={3} />}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* 底部模式提示 */}
      <footer className="p-4 text-center text-slate-400 text-xs tracking-widest uppercase">
        Digital A4 Canvas · Support iPad Gestures
      </footer>
    </div>
  );
}

