import React, { useState, useEffect, useMemo } from 'react';
import { Timer, X, Check, ArrowRight, RotateCcw, Settings, UserCheck, Eye, EyeOff } from 'lucide-react';

// --- MOCK DATABASE (模拟长期存储) ---
const INITIAL_WORD_BANK = [
  { id: '1', word: '山坡', pinyin: 'shān pō', unit: '单元1', grade: '三上' },
  { id: '2', word: '学校', pinyin: 'xué xiào', unit: '单元1', grade: '三上' },
  { id: '3', word: '飘扬', pinyin: 'piāo yáng', unit: '单元1', grade: '三上' },
  { id: '4', word: '课文', pinyin: 'kè wén', unit: '单元1', grade: '三上' },
  { id: '5', word: '声音', pinyin: 'shēng yīn', unit: '单元1', grade: '三上' },
  { id: '6', word: '招引', pinyin: 'zhāo yǐn', unit: '单元1', grade: '三上' },
];

export default function App() {
  // 1. 全局状态
  const [view, setView] = useState('SETUP'); // SETUP, PRACTICE, CORRECTION, FINAL
  const [sessionMode, setSessionMode] = useState('PRACTICE'); // PRACTICE (练习), SELF_TEST (自测)
  const [onlyWrong, setOnlyWrong] = useState(false); // 仅错题开关

  // 模拟持久化存储 (Mastery Data)
  const [mastery, setMastery] = useState(() => {
    const saved = localStorage.getItem('mastery_v1');
    return saved ? JSON.parse(saved) : {
      '3': { history: ['red', 'red'], status: 'red' } // 模拟“飘扬”以前错过
    };
  });

  // 2. 当前练习的数据
  const [currentWords, setCurrentWords] = useState([]);
  const [time, setTime] = useState(0);
  const [timerActive, setTimerActive] = useState(false);
  const [hintWordId, setHintWordId] = useState(null);

  // 3. 核心逻辑：选题
  const startSession = () => {
    let pool = [...INITIAL_WORD_BANK];
    if (onlyWrong) {
      // 逻辑：从未练过(无记录) OR 最近三次有红色的
      pool = pool.filter(w => {
        const record = mastery[w.id];
        return !record || record.status === 'red';
      });
    }

    // 初始化本次练习的本地状态
    setCurrentWords(pool.map(w => ({
      ...w,
      childMark: 'white',  // 孩子的红/白标记
      parentMark: 'white', // 家长的红/绿/白标记
    })));

    setTime(0);
    setTimerActive(true);
    setView('PRACTICE');
  };

  // 4. 计时器逻辑
  useEffect(() => {
    let interval;
    if (timerActive) {
      interval = setInterval(() => setTime(t => t + 1), 1000);
    }
    return () => clearInterval(interval);
  }, [timerActive]);

  // 5. 交互处理
  const handleChildMark = (id) => {
    setCurrentWords(prev => prev.map(w => 
      w.id === id ? { ...w, childMark: w.childMark === 'red' ? 'white' : 'red' } : w
    ));
  };

  const handleParentMark = (id) => {
    setCurrentWords(prev => prev.map(w => {
      if (w.id === id) {
        const next = { white: 'red', red: 'green', green: 'white' };
        return { ...w, parentMark: next[w.parentMark] };
      }
      return w;
    }));
  };

  const saveAndExit = () => {
    const newMastery = { ...mastery };
    currentWords.forEach(w => {
      newMastery[w.id] = {
        status: w.parentMark, // 以家长标记为准
        lastTime: time
      };
    });
    setMastery(newMastery);
    localStorage.setItem('mastery_v1', JSON.stringify(newMastery));
    setTimerActive(false);
    setView('SETUP');
  };

  // --- 子页面渲染 ---

  if (view === 'SETUP') {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center p-6 bg-slate-50">
        <div className="max-w-md w-full bg-white rounded-3xl shadow-xl p-8 border border-slate-100">
          <h1 className="text-3xl font-black text-slate-800 mb-8 italic">拼音听写 <span className="text-blue-600">V2.0</span></h1>

          <div className="space-y-6">
            <section>
              <label className="block text-sm font-bold text-slate-400 uppercase tracking-widest mb-3">练习模式</label>
              <div className="grid grid-cols-2 gap-3">
                <button 
                  onClick={() => setSessionMode('PRACTICE')}
                  className={`py-4 rounded-2xl font-bold transition-all border-2 ${sessionMode === 'PRACTICE' ? 'border-blue-500 bg-blue-50 text-blue-600' : 'border-slate-100 bg-slate-50 text-slate-400'}`}
                >自由练习</button>
                <button 
                  onClick={() => setSessionMode('SELF_TEST')}
                  className={`py-4 rounded-2xl font-bold transition-all border-2 ${sessionMode === 'SELF_TEST' ? 'border-blue-500 bg-blue-50 text-blue-600' : 'border-slate-100 bg-slate-50 text-slate-400'}`}
                >模拟自测</button>
              </div>
            </section>

            <section className="flex items-center justify-between py-4 border-y border-slate-50">
              <div>
                <div className="font-bold text-slate-700">仅练错题</div>
                <div className="text-xs text-slate-400">包含最近报错及未练过的词</div>
              </div>
              <button 
                onClick={() => setOnlyWrong(!onlyWrong)}
                className={`w-14 h-8 rounded-full transition-colors relative ${onlyWrong ? 'bg-orange-500' : 'bg-slate-200'}`}
              >
                <div className={`absolute top-1 w-6 h-6 bg-white rounded-full transition-all ${onlyWrong ? 'left-7' : 'left-1'}`} />
              </button>
            </section>

            <button 
              onClick={startSession}
              className="w-full bg-slate-900 text-white py-5 rounded-2xl font-black text-xl shadow-xl shadow-slate-200 active:scale-95 transition-all flex items-center justify-center gap-3"
            >
              开始练习 <ArrowRight size={24} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen overflow-hidden bg-slate-100">
      {/* 顶部导航 */}
      <header className="bg-white/90 backdrop-blur-md border-b px-6 py-4 flex justify-between items-center z-50 shrink-0 shadow-sm">
        <div className="flex items-center gap-4">
          <button onClick={() => setView('SETUP')} className="p-2 hover:bg-slate-100 rounded-full text-slate-400">
            <Settings size={20} />
          </button>
          <div className="h-6 w-[1px] bg-slate-200" />
          <span className="px-3 py-1 bg-blue-50 text-blue-600 rounded-full text-[10px] font-bold uppercase tracking-wider">
            {view === 'PRACTICE' ? (sessionMode === 'PRACTICE' ? '自由练习' : '模拟自测') : 
             view === 'CORRECTION' ? '自己对答案' : '家长终测'}
          </span>
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-2 font-mono text-xl font-bold text-slate-700 bg-slate-100 px-4 py-1.5 rounded-xl">
            <Timer size={20} className="text-blue-500" />
            {Math.floor(time / 60).toString().padStart(2, '0')}:{(time % 60).toString().padStart(2, '0')}
          </div>

          {view === 'PRACTICE' && (
            <button 
              onClick={() => setView(sessionMode === 'PRACTICE' ? 'CORRECTION' : 'CORRECTION')}
              className="bg-slate-900 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all"
            >完成书写</button>
          )}

          {view === 'CORRECTION' && (
            <button 
              onClick={() => setView('FINAL')}
              className="bg-blue-600 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all flex items-center gap-2"
            ><UserCheck size={18}/> 叫家长</button>
          )}

          {view === 'FINAL' && (
            <button 
              onClick={saveAndExit}
              className="bg-green-600 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all"
            >结束终测并存档</button>
          )}
        </div>
      </header>

      {/* 主画布 */}
      <main className="flex-1 overflow-y-auto p-4 md:p-12">
        <div className="a4-canvas p-12 md:p-20 rounded-md shadow-2xl border border-slate-200 relative min-h-screen">
          <div className="grid grid-cols-2 md:grid-cols-3 gap-x-12 gap-y-16">
            {currentWords.map((item, index) => (
              <div key={item.id} className="flex flex-col gap-4">
                {/* 1. 拼音展示区 */}
                <div 
                  className="relative select-none cursor-help active:opacity-70"
                  onPointerDown={() => {
                    if (view === 'PRACTICE' && sessionMode === 'PRACTICE') {
                      setHintWordId(item.id);
                      handleChildMark(item.id);
                    }
                  }}
                  onPointerUp={() => setHintWordId(null)}
                  onPointerLeave={() => setHintWordId(null)}
                >
                  <div className="flex flex-col">
                    <span className="text-3xl font-bold font-kaiti tracking-widest text-slate-800">
                      {item.pinyin}
                    </span>
                    {/* 校对态淡入显示汉字 */}
                    {(view === 'CORRECTION' || view === 'FINAL') && (
                      <span className="text-xl font-kaiti text-blue-500 font-medium mt-1 animate-in fade-in slide-in-from-top-1 duration-500">
                        {item.word}
                      </span>
                    )}
                  </div>

                  {/* 长按提示气泡 */}
                  {hintWordId === item.id && (
                    <div className="absolute -top-16 left-0 bg-blue-600 text-white text-4xl px-6 py-3 rounded-2xl shadow-2xl popover-up z-50 font-kaiti font-bold ring-4 ring-white">
                      {item.word}
                      <div className="absolute -bottom-2 left-6 w-4 h-4 bg-blue-600 rotate-45"></div>
                    </div>
                  )}
                </div>

                {/* 2. 方框操作区 */}
                <div className="flex items-center gap-4">
                  <div className="flex items-center shadow-sm w-fit group">
                    {/* 序号格 */}
                    <div className="tian-ge border-r-0 bg-slate-50 text-slate-900 text-[10px] font-mono font-bold">
                      {index + 1}
                    </div>

                    {/* 孩子标记格 (练习/自测/终测阶段均由孩子操作) */}
                    <button 
                      onClick={() => handleChildMark(item.id)}
                      className={`tian-ge border transition-all ${
                        item.childMark === 'red' ? 'bg-red-500 border-red-600 shadow-inner' : 'bg-white border-black'
                      }`}
                    />
                  </div>

                  {/* 3. 家长专属格 (仅在终测视图显示) */}
                  {view === 'FINAL' && (
                    <div className="flex items-center animate-in zoom-in duration-300">
                      <div className="w-4 h-[1px] bg-slate-300 mr-2" /> {/* 物理间距 */}
                      <button 
                        onClick={() => handleParentMark(item.id)}
                        className={`tian-ge border-2 flex items-center justify-center ${
                          item.parentMark === 'green' ? 'bg-green-500 border-green-600' :
                          item.parentMark === 'red' ? 'bg-red-500 border-red-600' :
                          'bg-white border-slate-400 border-dashed'
                        }`}
                      >
                        {item.parentMark === 'green' && <Check size={18} color="white" strokeWidth={4}/>}
                        {item.parentMark === 'red' && <X size={18} color="white" strokeWidth={4}/>}
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>

          {/* A4 底部水印 */}
          <div className="absolute bottom-8 left-0 right-0 text-center text-slate-200 font-black text-4xl pointer-events-none select-none">
            SEMIAUTO PRACTICE SHEET
          </div>
        </div>
      </main>
    </div>
  );
}

import React, { useState, useEffect, useMemo } from 'react';
import { Timer, X, Check, ArrowRight, Settings, UserCheck, ChevronRight, ListChecks } from 'lucide-react';

// --- MOCK DATABASE (模拟多层级词库) ---
const MOCK_GRADES = ['一年级上册', '二年级上册', '三年级上册'];
const MOCK_DATA = {
  '三年级上册': [
    { id: '1', word: '山坡', pinyin: 'shān pō', unit: '1' },
    { id: '2', word: '学校', pinyin: 'xué xiào', unit: '1' },
    { id: '3', word: '飘扬', pinyin: 'piāo yáng', unit: '1' },
    { id: '4', word: '课文', pinyin: 'kè wén', unit: '2' },
    { id: '5', word: '声音', pinyin: 'shēng yīn', unit: '2' },
    { id: '6', word: '招引', pinyin: 'zhāo yǐn', unit: '3' },
  ],
  '一年级上册': [
    { id: '101', word: '天地', pinyin: 'tiān dì', unit: '1' },
    { id: '102', word: '人口', pinyin: 'rén kǒu', unit: '1' },
  ]
};

export default function App() {
  // 1. 设置状态
  const [view, setView] = useState('SETUP'); 
  const [selectedGrade, setSelectedGrade] = useState('三年级上册');
  const [selectedUnits, setSelectedUnits] = useState(new Set(['1'])); // 默认选第一单元
  const [sessionMode, setSessionMode] = useState('PRACTICE'); 
  const [onlyWrong, setOnlyWrong] = useState(false);

  const [mastery, setMastery] = useState(() => {
    const saved = localStorage.getItem('mastery_v1');
    return saved ? JSON.parse(saved) : { '3': { status: 'red' } };
  });

  // 2. 练习实时状态
  const [currentWords, setCurrentWords] = useState([]);
  const [time, setTime] = useState(0);
  const [timerActive, setTimerActive] = useState(false);
  const [hintWordId, setHintWordId] = useState(null);

  // 获取当前教材下的单元列表
  const availableUnits = useMemo(() => {
    const units = MOCK_DATA[selectedGrade]?.map(w => w.unit) || [];
    return [...new Set(units)].sort();
  }, [selectedGrade]);

  // 选题逻辑
  const startSession = () => {
    let pool = MOCK_DATA[selectedGrade].filter(w => selectedUnits.has(w.unit));

    if (onlyWrong) {
      pool = pool.filter(w => {
        const record = mastery[w.id];
        return !record || record.status === 'red';
      });
    }

    if (pool.length === 0) {
      alert("所选范围下没有符合条件的题目哦！");
      return;
    }

    setCurrentWords(pool.map(w => ({ ...w, childMark: 'white', parentMark: 'white' })));
    setTime(0);
    setTimerActive(true);
    setView('PRACTICE');
  };

  const toggleUnit = (unit) => {
    const newSelected = new Set(selectedUnits);
    if (newSelected.has(unit)) newSelected.delete(unit);
    else newSelected.add(unit);
    setSelectedUnits(newSelected);
  };

  const selectAllUnits = () => {
    if (selectedUnits.size === availableUnits.length) setSelectedUnits(new Set());
    else setSelectedUnits(new Set(availableUnits));
  };

  // --- 渲染逻辑 ---

  if (view === 'SETUP') {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center p-4">
        <div className="max-w-xl w-full bg-white rounded-[2.5rem] shadow-2xl p-10 border border-slate-100">
          <header className="mb-10 text-center">
            <h1 className="text-4xl font-black text-slate-800 italic">
              听写训练 <span className="text-blue-600">V2.0</span>
            </h1>
            <p className="text-slate-400 mt-2 font-medium">定制您的 iPad 互动练习</p>
          </header>

          <div className="space-y-8">
            {/* 1. 选择教材 */}
            <section>
              <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3 block">1. 选择教材</label>
              <div className="relative">
                <select 
                  value={selectedGrade}
                  onChange={(e) => { setSelectedGrade(e.target.value); setSelectedUnits(new Set(['1'])); }}
                  className="w-full bg-slate-50 border-2 border-slate-100 rounded-2xl px-6 py-4 font-bold text-slate-700 appearance-none focus:border-blue-500 focus:outline-none transition-all"
                >
                  {MOCK_GRADES.map(g => <option key={g} value={g}>{g}</option>)}
                </select>
                <div className="absolute right-6 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                  <ChevronRight size={20} className="rotate-90" />
                </div>
              </div>
            </section>

            {/* 2. 选择单元 */}
            <section>
              <div className="flex justify-between items-end mb-3">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">2. 练习范围</label>
                <button onClick={selectAllUnits} className="text-xs font-bold text-blue-600 hover:text-blue-700">
                  {selectedUnits.size === availableUnits.length ? '取消全选' : '全选单元'}
                </button>
              </div>
              <div className="flex flex-wrap gap-2">
                {availableUnits.map(u => (
                  <button
                    key={u}
                    onClick={() => toggleUnit(u)}
                    className={`px-5 py-2.5 rounded-xl font-bold transition-all border-2 ${
                      selectedUnits.has(u) 
                        ? 'border-blue-500 bg-blue-500 text-white shadow-md shadow-blue-100' 
                        : 'border-slate-100 bg-slate-50 text-slate-400'
                    }`}
                  >
                    第 {u} 单元
                  </button>
                ))}
              </div>
            </section>

            {/* 3. 模式与开关 */}
            <section className="grid grid-cols-2 gap-6 py-6 border-y border-slate-50">
              <div>
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3 block">练习模式</label>
                <div className="flex bg-slate-100 p-1 rounded-xl">
                  <button 
                    onClick={() => setSessionMode('PRACTICE')}
                    className={`flex-1 py-2 rounded-lg text-sm font-bold transition-all ${sessionMode === 'PRACTICE' ? 'bg-white text-blue-600 shadow-sm' : 'text-slate-400'}`}
                  >自由</button>
                  <button 
                    onClick={() => setSessionMode('SELF_TEST')}
                    className={`flex-1 py-2 rounded-lg text-sm font-bold transition-all ${sessionMode === 'SELF_TEST' ? 'bg-white text-blue-600 shadow-sm' : 'text-slate-400'}`}
                  >自测</button>
                </div>
              </div>
              <div className="flex flex-col justify-center items-end">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-2 block">仅练错题</label>
                <button 
                  onClick={() => setOnlyWrong(!onlyWrong)}
                  className={`w-12 h-7 rounded-full transition-colors relative ${onlyWrong ? 'bg-orange-500' : 'bg-slate-200'}`}
                >
                  <div className={`absolute top-1 w-5 h-5 bg-white rounded-full transition-all ${onlyWrong ? 'left-6' : 'left-1'}`} />
                </button>
              </div>
            </section>

            <button 
              onClick={startSession}
              disabled={selectedUnits.size === 0}
              className="w-full bg-slate-900 text-white py-5 rounded-[1.5rem] font-black text-xl shadow-2xl shadow-slate-200 active:scale-95 disabled:opacity-30 transition-all flex items-center justify-center gap-3"
            >
              进入书写画布 <ArrowRight size={24} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  // --- 后续页面逻辑 (保持不变但使用动态 currentWords) ---
  return (
    <div className="flex flex-col h-screen overflow-hidden bg-slate-100">
      <header className="bg-white/90 backdrop-blur-md border-b px-6 py-4 flex justify-between items-center z-50 shrink-0 shadow-sm">
        <div className="flex items-center gap-4">
          <button onClick={() => setView('SETUP')} className="p-2 hover:bg-slate-100 rounded-full text-slate-400 transition-colors">
            <Settings size={20} />
          </button>
          <div className="h-6 w-[1px] bg-slate-200" />
          <div className="flex flex-col">
            <span className="text-xs font-black text-slate-800 tracking-tight">{selectedGrade}</span>
            <span className="text-[10px] text-blue-500 font-bold uppercase tracking-widest">
              {view === 'PRACTICE' ? (sessionMode === 'PRACTICE' ? '自由练习' : '模拟自测') : 
               view === 'CORRECTION' ? '自己对答案' : '家长终测'}
            </span>
          </div>
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-2 font-mono text-xl font-bold text-slate-700 bg-slate-100 px-4 py-1.5 rounded-xl">
            <Timer size={20} className="text-blue-500" />
            {Math.floor(time / 60).toString().padStart(2, '0')}:{(time % 60).toString().padStart(2, '0')}
          </div>

          {view === 'PRACTICE' && (
            <button 
              onClick={() => setView('CORRECTION')}
              className="bg-slate-900 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all"
            >完成书写</button>
          )}

          {view === 'CORRECTION' && (
            <button 
              onClick={() => setView('FINAL')}
              className="bg-blue-600 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all flex items-center gap-2"
            ><UserCheck size={18}/> 叫家长</button>
          )}

          {view === 'FINAL' && (
            <button 
              onClick={() => {
                const newMastery = { ...mastery };
                currentWords.forEach(w => {
                  newMastery[w.id] = { status: w.parentMark, lastTime: time };
                });
                setMastery(newMastery);
                localStorage.setItem('mastery_v1', JSON.stringify(newMastery));
                setTimerActive(false);
                setView('SETUP');
              }}
              className="bg-green-600 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all"
            >结束并存档</button>
          )}
        </div>
      </header>

      <main className="flex-1 overflow-y-auto p-4 md:p-12">
        <div className="a4-canvas p-12 md:p-20 rounded-md shadow-2xl border border-slate-200 relative min-h-screen">
          <div className="grid grid-cols-2 md:grid-cols-3 gap-x-12 gap-y-16">
            {currentWords.map((item, index) => (
              <div key={item.id} className="flex flex-col gap-4">
                <div 
                  className="relative select-none cursor-help active:opacity-70"
                  onPointerDown={() => {
                    if (view === 'PRACTICE' && sessionMode === 'PRACTICE') {
                      setHintWordId(item.id);
                      if (item.childMark !== 'red') {
                        setCurrentWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: 'red' } : w));
                      }
                    }
                  }}
                  onPointerUp={() => setHintWordId(null)}
                  onPointerLeave={() => setHintWordId(null)}
                >
                  <div className="flex flex-col">
                    <span className="text-3xl font-bold font-kaiti tracking-widest text-slate-800">
                      {item.pinyin}
                    </span>
                    {(view === 'CORRECTION' || view === 'FINAL') && (
                      <span className="text-xl font-kaiti text-blue-500 font-medium mt-1 animate-in fade-in slide-in-from-top-1 duration-500">
                        {item.word}
                      </span>
                    )}
                  </div>

                  {hintWordId === item.id && (
                    <div className="absolute -top-16 left-0 bg-blue-600 text-white text-4xl px-6 py-3 rounded-2xl shadow-2xl popover-up z-50 font-kaiti font-bold ring-4 ring-white">
                      {item.word}
                      <div className="absolute -bottom-2 left-6 w-4 h-4 bg-blue-600 rotate-45"></div>
                    </div>
                  )}
                </div>

                <div className="flex items-center gap-4">
                  <div className="flex items-center shadow-sm w-fit group">
                    <div className="tian-ge border-r-0 bg-slate-50 text-slate-900 text-[10px] font-mono font-bold">{index + 1}</div>
                    <button 
                      onClick={() => setCurrentWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: w.childMark === 'red' ? 'white' : 'red' } : w))}
                      className={`tian-ge border transition-all ${item.childMark === 'red' ? 'bg-red-500 border-red-600 shadow-inner' : 'bg-white border-black'}`}
                    />
                  </div>

                  {view === 'FINAL' && (
                    <div className="flex items-center animate-in zoom-in duration-300">
                      <div className="w-4 h-[1px] bg-slate-300 mr-2" />
                      <button 
                        onClick={() => {
                          const next = { white: 'red', red: 'green', green: 'white' };
                          setCurrentWords(prev => prev.map(w => w.id === item.id ? { ...w, parentMark: next[w.parentMark] } : w));
                        }}
                        className={`tian-ge border-2 flex items-center justify-center ${
                          item.parentMark === 'green' ? 'bg-green-500 border-green-600' :
                          item.parentMark === 'red' ? 'bg-red-500 border-red-600' :
                          'bg-white border-slate-400 border-dashed'
                        }`}
                      >
                        {item.parentMark === 'green' && <Check size={18} color="white" strokeWidth={4}/>}
                        {item.parentMark === 'red' && <X size={18} color="white" strokeWidth={4}/>}
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

