import React, { useState, useEffect, useMemo } from 'react';
import { Timer, X, Check, ArrowRight, Settings, UserCheck, ChevronRight, ChevronDown, ListChecks, GraduationCap } from 'lucide-react';

// --- 更加真实的 MOCK 数据结构 ---
const MOCK_DATA = {
  '三年级上册': [
    { id: '1', word: '山坡', pinyin: 'shān pō', unit: '1' },
    { id: '2', word: '学校', pinyin: 'xué xiào', unit: '1' },
    { id: '3', word: '飘扬', pinyin: 'piāo yáng', unit: '1' },
    { id: '4', word: '课文', pinyin: 'kè wén', unit: '1' },
    { id: '5', word: '声音', pinyin: 'shēng yīn', unit: '2' },
    { id: '6', word: '招引', pinyin: 'zhāo yǐn', unit: '2' },
    { id: '7', word: '热闹', pinyin: 'rè nao', unit: '2' },
    { id: '8', word: '古老', pinyin: 'gǔ lǎo', unit: '3' },
  ],
  '一年级上册': [
    { id: '101', word: '天地', pinyin: 'tiān dì', unit: '1' },
    { id: '102', word: '人口', pinyin: 'rén kǒu', unit: '1' },
  ]
};

export default function App() {
  // 1. 全局与设置状态
  const [view, setView] = useState('SETUP'); 
  const [selectedGrade, setSelectedGrade] = useState('三年级上册');
  const [selectedWords, setSelectedWords] = useState([]); // 选中的词语对象数组
  const [expandedUnits, setExpandedUnits] = useState({});
  const [sessionMode, setSessionMode] = useState('PRACTICE'); 
  const [onlyWrong, setOnlyWrong] = useState(false);

  // 掌握度数据库
  const [mastery, setMastery] = useState(() => {
    const saved = localStorage.getItem('mastery_v1');
    return saved ? JSON.parse(saved) : { '3': { status: 'red', history: ['red'] } };
  });

  // 2. 练习状态
  const [currentSessionWords, setCurrentSessionWords] = useState([]);
  const [time, setTime] = useState(0);
  const [timerActive, setTimerActive] = useState(false);
  const [hintWordId, setHintWordId] = useState(null);

  // 按单元分组当前教材的词汇
  const units = useMemo(() => {
    const groups = {};
    (MOCK_DATA[selectedGrade] || []).forEach(word => {
      if (!groups[word.unit]) groups[word.unit] = [];
      groups[word.unit].push(word);
    });
    return groups;
  }, [selectedGrade]);

  // 3. 核心逻辑
  const toggleWord = (word) => {
    setSelectedWords(prev => 
      prev.find(w => w.id === word.id) 
        ? prev.filter(w => w.id !== word.id)
        : [...prev, word]
    );
  };

  const toggleUnit = (unit) => {
    const unitWords = units[unit];
    const allSelected = unitWords.every(uw => selectedWords.find(sw => sw.id === uw.id));
    if (allSelected) {
      setSelectedWords(prev => prev.filter(sw => !unitWords.find(uw => uw.id === sw.id)));
    } else {
      const newWords = unitWords.filter(uw => !selectedWords.find(sw => sw.id === uw.id));
      setSelectedWords(prev => [...prev, ...newWords]);
    }
  };

  const startSession = () => {
    let pool = [...selectedWords];
    if (onlyWrong) {
      // 策略：未练过(无Mastery记录) OR 最近三次被家长标过红
      pool = pool.filter(w => {
        const record = mastery[w.id];
        return !record || record.status === 'red';
      });
    }
    if (pool.length === 0) { alert("所选范围内没有需要练习的题目！"); return; }

    setCurrentSessionWords(pool.map(w => ({ ...w, childMark: 'white', parentMark: 'white' })));
    setTime(0);
    setTimerActive(true);
    setView('PRACTICE');
  };

  useEffect(() => {
    let interval;
    if (timerActive) interval = setInterval(() => setTime(t => t + 1), 1000);
    return () => clearInterval(interval);
  }, [timerActive]);

  // --- 子组件: 选题页 (复刻线下版风格) ---
  if (view === 'SETUP') {
    return (
      <div className="min-h-screen bg-slate-50 overflow-y-auto pb-32 font-sans">
        <div className="max-w-4xl mx-auto p-6 md:p-10">
          <header className="flex justify-between items-center mb-10">
            <div>
              <h1 className="text-3xl font-black text-slate-800 tracking-tight">听写范围设置</h1>
              <p className="text-slate-400 text-sm font-bold uppercase tracking-widest mt-1">Semiauto Pinyin v2</p>
            </div>
            <div className="flex gap-3">
              <select 
                value={selectedGrade}
                onChange={(e) => { setSelectedGrade(e.target.value); setSelectedWords([]); }}
                className="bg-white border-2 border-slate-200 rounded-xl px-4 py-2 font-bold text-slate-700 outline-none focus:border-blue-500 transition-all"
              >
                {Object.keys(MOCK_DATA).map(g => <option key={g} value={g}>{g}</option>)}
              </select>
            </div>
          </header>

          {/* 全局控制开关 */}
          <div className="flex gap-6 mb-8 bg-blue-600 p-6 rounded-[2rem] text-white shadow-xl shadow-blue-100">
            <div className="flex-1 border-r border-blue-400/50 pr-6">
              <label className="text-[10px] font-black uppercase tracking-widest opacity-70 mb-2 block">练习模式</label>
              <div className="flex bg-blue-700/50 p-1 rounded-xl">
                {['PRACTICE', 'SELF_TEST'].map(m => (
                  <button key={m} onClick={() => setSessionMode(m)} className={`flex-1 py-2 rounded-lg text-xs font-bold transition-all ${sessionMode === m ? 'bg-white text-blue-600' : 'text-blue-100'}`}>
                    {m === 'PRACTICE' ? '自由练习' : '模拟自测'}
                  </button>
                ))}
              </div>
            </div>
            <div className="flex flex-col justify-center items-center px-4">
              <label className="text-[10px] font-black uppercase tracking-widest opacity-70 mb-2 block text-center">仅练错题</label>
              <button 
                onClick={() => setOnlyWrong(!onlyWrong)}
                className={`w-14 h-8 rounded-full transition-colors relative ${onlyWrong ? 'bg-orange-400' : 'bg-blue-800/50'}`}
              >
                <div className={`absolute top-1 w-6 h-6 bg-white rounded-full transition-all ${onlyWrong ? 'left-7' : 'left-1'}`} />
              </button>
            </div>
          </div>

          {/* 单元列表 (复刻线下版核心组件) */}
          <div className="bg-white rounded-3xl shadow-lg border border-slate-100 overflow-hidden">
            {Object.entries(units).map(([unit, unitWords]) => {
              const isExpanded = expandedUnits[unit];
              const selectedInUnit = unitWords.filter(uw => selectedWords.find(sw => sw.id === uw.id)).length;
              const isAllSelected = selectedInUnit === unitWords.length;

              return (
                <div key={unit} className="border-b last:border-0">
                  <div className="flex items-center p-5 hover:bg-slate-50 transition-colors">
                    <button 
                      onClick={() => setExpandedUnits(prev => ({ ...prev, [unit]: !prev[unit] }))} 
                      className="mr-4 p-1 hover:bg-slate-200 rounded-lg transition-colors"
                    >
                      {isExpanded ? <ChevronDown size={20} /> : <ChevronRight size={20} />}
                    </button>
                    <div className="flex-1 cursor-pointer" onClick={() => setExpandedUnits(prev => ({ ...prev, [unit]: !prev[unit] }))}>
                      <span className="font-black text-slate-700">第 {unit} 单元</span>
                      <span className="ml-4 text-xs font-bold text-slate-400 bg-slate-100 px-2 py-1 rounded">已选 {selectedInUnit} / {unitWords.length}</span>
                    </div>
                    <button 
                      onClick={() => toggleUnit(unit)}
                      className={`flex items-center gap-1 px-4 py-1.5 rounded-full text-xs font-bold transition-all ${
                        isAllSelected ? 'bg-green-100 text-green-700' : 'bg-slate-100 text-slate-500 hover:bg-slate-200'
                      }`}
                    >
                      <ListChecks size={14} /> {isAllSelected ? '取消全选' : '全选单元'}
                    </button>
                  </div>

                  {isExpanded && (
                    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3 p-6 bg-slate-50/50">
                      {unitWords.map(word => {
                        const isSelected = selectedWords.find(sw => sw.id === word.id);
                        return (
                          <div 
                            key={word.id}
                            onClick={() => toggleWord(word)}
                            className={`flex items-center p-3 rounded-xl border-2 cursor-pointer transition-all ${
                              isSelected ? 'border-blue-500 bg-white shadow-md shadow-blue-100' : 'border-white bg-white/50 hover:border-slate-200'
                            }`}
                          >
                            <div className={`w-5 h-5 rounded flex items-center justify-center border-2 mr-3 transition-colors ${
                              isSelected ? 'bg-blue-500 border-blue-500' : 'border-slate-200'
                            }`}>
                              {isSelected && <Check size={14} color="white" strokeWidth={4} />}
                            </div>
                            <div>
                              <div className="text-sm font-black text-slate-700">{word.word}</div>
                              <div className="text-[10px] text-slate-400 font-mono">{word.pinyin}</div>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* 底部浮动开始按钮 */}
        <div className="fixed bottom-0 left-0 right-0 p-6 bg-slate-50/80 backdrop-blur-lg border-t border-slate-200 z-[60]">
          <button 
            onClick={startSession}
            disabled={selectedWords.length === 0}
            className="max-w-4xl mx-auto w-full bg-slate-900 text-white py-5 rounded-[1.5rem] font-black text-xl shadow-2xl shadow-slate-300 active:scale-95 disabled:opacity-30 transition-all flex items-center justify-center gap-3"
          >
            进入书写画布 ({selectedWords.length}) <ArrowRight size={24} />
          </button>
        </div>
      </div>
    );
  }

  // --- 主书写画布 (练习/自测/终测) ---
  return (
    <div className="flex flex-col h-screen overflow-hidden bg-slate-100 font-sans">
      <header className="bg-white/90 backdrop-blur-md border-b px-6 py-4 flex justify-between items-center z-50 shrink-0 shadow-sm">
        <div className="flex items-center gap-4">
          <button onClick={() => setView('SETUP')} className="p-2 hover:bg-slate-100 rounded-full text-slate-400"><Settings size={20} /></button>
          <div className="h-6 w-[1px] bg-slate-200" />
          <div className="flex flex-col">
            <span className="text-xs font-black text-slate-800">{selectedGrade}</span>
            <span className="text-[10px] text-blue-500 font-bold uppercase tracking-widest">
              {view === 'PRACTICE' ? (sessionMode === 'PRACTICE' ? '自由练习' : '模拟自测') : 
               view === 'CORRECTION' ? '自己对答案' : '家长终测'}
            </span>
          </div>
        </div>

