          <div className="flex items-center gap-2 font-mono text-xl font-bold text-slate-700 bg-slate-100 px-4 py-1.5 rounded-xl">
            <Timer size={20} className="text-blue-500" />
            {Math.floor(time / 60).toString().padStart(2, '0')}:{(time % 60).toString().padStart(2, '0')}
          </div>

          {view === 'PRACTICE' && (
            <button onClick={() => setView('CORRECTION')} className="bg-slate-900 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all">完成书写</button>
          )}

          {view === 'CORRECTION' && (
            <button onClick={() => setView('FINAL')} className="bg-blue-600 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all flex items-center gap-2"><UserCheck size={18}/> 叫家长</button>
          )}

          {view === 'FINAL' && (
            <button 
              onClick={() => {
                const newMastery = { ...mastery };
                currentSessionWords.forEach(w => {
                  newMastery[w.id] = { status: w.parentMark, lastTime: time };
                });
                setMastery(newMastery);
                localStorage.setItem('mastery_v1', JSON.stringify(newMastery));
                setTimerActive(false);
                setView('SETUP');
              }}
              className="bg-green-600 text-white px-6 py-2 rounded-xl font-bold shadow-lg active:scale-95 transition-all"
            >结束并存档</button>
          )}
        </div>
      </header>

      <main className="flex-1 overflow-y-auto p-4 md:p-12">
        <div className="a4-canvas p-12 md:p-20 rounded-md shadow-2xl relative min-h-screen">
          <div className="grid grid-cols-2 md:grid-cols-3 gap-x-12 gap-y-16">
            {currentSessionWords.map((item, index) => (
              <div key={item.id} className="flex flex-col gap-4">
                <div 
                  className="relative select-none cursor-help active:opacity-70"
                  onPointerDown={() => {
                    if (view === 'PRACTICE' && sessionMode === 'PRACTICE') {
                      setHintWordId(item.id);
                      if (item.childMark !== 'red') {
                        setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: 'red' } : w));
                      }
                    }
                  }}
                  onPointerUp={() => setHintWordId(null)}
                  onPointerLeave={() => setHintWordId(null)}
                >
                  <div className="flex flex-col">
                    <span className="text-3xl font-bold kaiti tracking-widest text-slate-800">
                      {item.pinyin}
                    </span>
                    {(view === 'CORRECTION' || view === 'FINAL') && (
                      <span className="text-xl kaiti text-blue-500 font-medium mt-1 animate-in fade-in slide-in-from-top-1 duration-500">
                        {item.word}
                      </span>
                    )}
                  </div>

                  {hintWordId === item.id && (
                    <div className="absolute -top-16 left-0 bg-blue-600 text-white text-4xl px-6 py-3 rounded-2xl shadow-2xl popover-up z-50 kaiti font-bold ring-4 ring-white">
                      {item.word}
                      <div className="absolute -bottom-2 left-6 w-4 h-4 bg-blue-600 rotate-45"></div>
                    </div>
                  )}
                </div>

                <div className="flex items-center gap-4">
                  <div className="flex items-center w-fit">
                    <div className="tian-ge border-r-0 bg-slate-50 text-slate-900 text-[10px] font-mono font-bold flex items-center justify-center italic">
                      {index + 1}
                    </div>
                    <button 
                      onClick={() => setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: w.childMark === 'red' ? 'white' : 'red' } : w))}
                      className={`tian-ge transition-all ${
                        item.childMark === 'red' ? 'bg-red-500 border-red-600' : 'bg-white border-black'
                      }`}
                    />
                  </div>

                  {view === 'FINAL' && (
                    <div className="flex items-center animate-in zoom-in duration-300">
                      <div className="w-4 h-[1px] bg-slate-300 mr-2" />
                      <button 
                        onClick={() => {
                          const next = { white: 'red', red: 'green', green: 'white' };
                          setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, parentMark: next[w.parentMark] } : w));
                        }}
                        className={`tian-ge border-2 flex items-center justify-center ${
                          item.parentMark === 'green' ? 'bg-green-500 border-green-600' :
                          item.parentMark === 'red' ? 'bg-red-500 border-red-600' :
                          'bg-white border-slate-400 border-dashed'
                        }`}
                      >
                        {item.parentMark === 'green' && <Check size={18} color="white" strokeWidth={4}/>}
                        {item.parentMark === 'red' && <X size={18} color="white" strokeWidth={4}/>}
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

