import React, { useState, useEffect, useMemo } from 'react';
import { Timer, X, Check, ArrowRight, Settings, UserCheck, ChevronRight, ListChecks, HelpCircle } from 'lucide-react';

// --- 大型 MOCK 数据生成器 (100+ 词组) ---
const GENERATE_MOCK = () => {
  const units = {};
  for (let u = 1; u <= 10; u++) {
    const unitKey = `单元${u}`;
    units[unitKey] = [];
    for (let w = 1; w <= 12; w++) {
      units[unitKey].push({
        id: `u${u}-w${w}`,
        word: `词组${u}-${w}`,
        pinyin: `cí zǔ ${u} ${w}`,
        unit: unitKey
      });
    }
  }
  // 特殊处理语文园地
  units['语文园地1'] = [
    { id: 'y1-1', word: '恒心', pinyin: 'héng xīn', unit: '语文园地1' },
    { id: 'y1-2', word: '神圣', pinyin: 'shén shèng', unit: '语文园地1' },
    { id: 'y1-3', word: '萌发', pinyin: 'méng fā', unit: '语文园地1' },
  ];
  return units;
};

const MOCK_DATA_POOL = {
  '三年级上册': GENERATE_MOCK(),
  '一年级上册': { '单元1': [{ id: '101', word: '天地', pinyin: 'tiān dì', unit: '单元1' }] }
};

