          </div>
          <div className="flex items-center gap-6">
            <div className="flex bg-slate-100 p-1 rounded-xl">
              <button onClick={() => setSessionMode('PRACTICE')} className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${sessionMode === 'PRACTICE' ? 'bg-white shadow-sm text-blue-600' : 'text-slate-400'}`}>自由练习</button>
              <button onClick={() => setSessionMode('SELF_TEST')} className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${sessionMode === 'SELF_TEST' ? 'bg-white shadow-sm text-blue-600' : 'text-slate-400'}`}>模拟自测</button>
            </div>
            <div className="flex items-center gap-2 border-l pl-6">
              <span className="text-xs font-bold text-slate-500 italic">仅练错题</span>
              <button onClick={() => setOnlyWrong(!onlyWrong)} className={`w-10 h-6 rounded-full transition-colors relative ${onlyWrong ? 'bg-orange-500' : 'bg-slate-200'}`}>
                <div className={`absolute top-1 w-4 h-4 bg-white rounded-full transition-all ${onlyWrong ? 'left-5' : 'left-1'}`} />
              </button>
            </div>
            <select value={selectedGrade} onChange={(e) => setSelectedGrade(e.target.value)} className="text-sm font-black text-blue-600 border-none bg-transparent">
              {Object.keys(MOCK_DATA_POOL).map(g => <option key={g} value={g}>{g}</option>)}
            </select>
          </div>
        </header>

        <div className="max-w-6xl mx-auto px-8 py-8">
          <div className="bg-white border-2 border-slate-200 rounded-2xl overflow-hidden">
            {Object.entries(units).map(([uName, uWords]) => (
              <div key={uName} className="flex items-start p-4 border-b last:border-0 hover:bg-slate-50 transition-colors">
                <input type="checkbox" checked={selectedUnitNames.has(uName)} onChange={() => {
                  const next = new Set(selectedUnitNames);
                  if (next.has(uName)) next.delete(uName); else next.add(uName);
                  setSelectedUnits(next);
                }} className="w-5 h-5 rounded mt-0.5 border-slate-300 text-blue-600" />
                <div className="w-28 shrink-0 px-4"><span className="text-sm font-black text-slate-700">{uName}</span></div>
                <div className="flex-1 text-sm text-slate-400 font-medium tracking-wide">
                  {uWords.map(w => w.word).join('、')}
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="fixed bottom-0 left-0 right-0 p-8 bg-gradient-to-t from-slate-50 to-transparent flex justify-center pointer-events-none">
          <button onClick={startSession} className="pointer-events-auto bg-slate-900 text-white px-16 py-5 rounded-[2rem] font-black text-xl shadow-2xl active:scale-95 transition-all flex items-center gap-4 disabled:opacity-20" disabled={selectedUnitNames.size === 0}>
            开始书写 ({Array.from(selectedUnitNames).reduce((acc, curr) => acc + (units[configs] || units[curr] || []).length, 0)} 词) <ArrowRight size={24} />
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen overflow-hidden bg-slate-100 font-sans">
      <header className="bg-white border-b px-8 py-4 flex justify-between items-center z-50 shrink-0 shadow-md">
        <div className="flex items-center gap-6">
          <button onClick={() => setView('SETUP')} className="text-slate-300 hover:text-slate-900 transition-colors"><Settings size={22} /></button>
          <div className="h-8 w-[1.5px] bg-slate-100" />
          <ModeBadge />
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-3 font-mono text-2xl font-black text-slate-700 bg-slate-50 px-6 py-2 rounded-2xl border-2 border-slate-100">
            <Timer size={22} className="text-blue-500" />
            {Math.floor(time / 60).toString().padStart(2, '0')}:{(time % 60).toString().padStart(2, '0')}
          </div>
          <button 
            onClick={() => {
              if (view === 'PRACTICE') setView('CORRECTION');
              else if (view === 'CORRECTION') setView('FINAL');
              else {
                const newMastery = { ...mastery };
                currentSessionWords.forEach(w => { newMastery[w.id] = { status: w.parentMark, time }; });
                setMastery(newMastery);
                localStorage.setItem('mastery_v1', JSON.stringify(newMastery));
                setTimerActive(false); setView('SETUP');
              }
            }}
            className={`${view === 'FINAL' ? 'bg-green-600' : view === 'CORRECTION' ? 'bg-orange-500' : 'bg-slate-900'} text-white px-10 py-3 rounded-2xl font-black shadow-lg active:scale-95 transition-all`}
          >
            {view === 'PRACTICE' ? '结束书写' : view === 'CORRECTION' ? '叫家长批改' : '完成并同步'}
          </button>
        </div>
      </header>

      <main className="flex-1 overflow-y-auto p-8 lg:p-16 scroll-smooth">
        <div className="a4-canvas p-16 lg:p-24 rounded-sm relative min-h-screen">
          <div className="grid grid-cols-2 lg:grid-cols-3 gap-x-12 gap-y-24">
            {currentSessionWords.map((item, index) => (
              <div key={item.id} className="flex flex-col gap-5">
                {/* 拼音与交互 */}
                <div 
                  className="relative select-none group"
                  onPointerDown={() => {
                    if (view === 'PRACTICE' && sessionMode === 'PRACTICE') {
                      setHintWordId(item.id);
                      // 【核心逻辑】长按必然标记为红色，不管之前是什么颜色
                      setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: 'red' } : w));
                    }
                  }}
                  onPointerUp={() => setHintWordId(null)}
                  onPointerLeave={() => setHintWordId(null)}
                >
                  <div className="flex flex-col">
                    <span className="text-4xl font-bold font-kaiti tracking-widest text-slate-800 leading-none">
                      {item.pinyin}
                    </span>
                    {(view === 'CORRECTION' || view === 'FINAL') && (
                      <span className="text-2xl font-kaiti text-blue-600 font-black mt-3 animate-in fade-in slide-in-from-top-2 duration-700">
                        {item.word}
                      </span>
                    )}
                  </div>

                  {hintWordId === item.id && (
                    <div className="absolute -top-20 left-0 bg-blue-600 text-white text-5xl px-8 py-4 rounded-[2rem] shadow-2xl z-50 font-kaiti font-black ring-8 ring-white popover-up flex items-center gap-4">
                      {item.word}
                      <Check size={32} />
                      <div className="absolute -bottom-3 left-10 w-6 h-6 bg-blue-600 rotate-45"></div>
                    </div>
                  )}
                </div>

                {/* 方格组 */}
                <div className="flex items-center gap-6">
                  <div className="flex items-center">
                    <div className="tian-ge border-r-0 bg-slate-50 text-slate-900 text-xs font-mono font-black flex items-center justify-center italic">
                      {index + 1}
                    </div>
                    <button 
                      onClick={() => setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: w.childMark === 'red' ? 'white' : 'red' } : w))}
                      className={`tian-ge transition-all ${item.childMark === 'red' ? 'bg-red-500 border-red-600 shadow-inner scale-110' : 'bg-white border-black'}`}
                    />
                  </div>

                  {view === 'FINAL' && (
                    <div className="flex items-center animate-in slide-in-from-left-4 duration-500">
                      <div className="w-6 h-[2px] bg-slate-200 mr-2" />
                      <button 
                        onClick={() => {
                          const next = { white: 'red', red: 'green', green: 'white' };
                          setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, parentMark: next[w.parentMark] } : w));
                        }}
                        className={`tian-ge border-2 flex items-center justify-center scale-110 ${
                          item.parentMark === 'green' ? 'bg-green-500 border-green-600 shadow-green-100' :
                          item.parentMark === 'red' ? 'bg-red-500 border-red-600 shadow-red-100' :
                          'bg-white border-slate-300 border-dashed'
                        } shadow-xl`}
                      >
                        {item.parentMark === 'green' && <Check size={20} color="white" strokeWidth={5}/>}
                        {item.parentMark === 'red' && <X size={20} color="white" strokeWidth={5}/>}
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

import React, { useState, useEffect, useMemo } from 'react';
import { Timer, X, Check, ArrowRight, Settings, UserCheck, ChevronRight, ListChecks, HelpCircle } from 'lucide-react';

// --- 大型 MOCK 数据生成器 ---
const GENERATE_MOCK = () => {
  const units = {};
  for (let u = 1; u <= 10; u++) {
    const unitKey = `单元${u}`;
    units[unitKey] = [];
    for (let w = 1; w <= 12; w++) {
      units[unitKey].push({
        id: `u${u}-w${w}`,
        word: `词组${u}-${w}`,
        pinyin: `cí zǔ ${u} ${w}`,
        unit: unitKey
      });
    }
  }
  return units;
};

const MOCK_DATA_POOL = {
  '三年级上册': GENERATE_MOCK(),
  '一年级上册': { '单元1': [{ id: '101', word: '天地', pinyin: 'tiān dì', unit: '单元1' }] }
};

export default function App() {
  const [view, setView] = useState('SETUP');
  const [selectedGrade, setSelectedGrade] = useState('三年级上册');
  const [selectedUnitNames, setSelectedUnits] = useState(new Set(['单元1']));
  const [sessionMode, setSessionMode] = useState('PRACTICE'); 
  const [onlyWrong, setOnlyWrong] = useState(false);

  const [mastery, setMastery] = useState(() => {
    const saved = localStorage.getItem('mastery_v1');
    return saved ? JSON.parse(saved) : {};
  });

  const [currentSessionWords, setCurrentSessionWords] = useState([]);
  const [time, setTime] = useState(0);
  const [timerActive, setTimerActive] = useState(false);
  const [hintWordId, setHintWordId] = useState(null);

  const units = MOCK_DATA_POOL[selectedGrade] || {};

  const startSession = () => {
    let pool = [];
    selectedUnitNames.forEach(name => { 
      if (units[name]) pool = [...pool, ...units[name]];
    });

    if (onlyWrong) {
      pool = pool.filter(w => !mastery[w.id] || mastery[w.id].status === 'red');
    }

    if (pool.length === 0) {
      alert("没有符合条件的题目！");
      return;
    }

    const prepared = pool.map(w => ({ ...w, childMark: 'white', parentMark: 'white' }));
    setCurrentSessionWords(prepared);
    setTime(0);
    setTimerActive(true);
    setView('PRACTICE');
  };

  useEffect(() => {
    let interval;
    if (timerActive) interval = setInterval(() => setTime(t => t + 1), 1000);
    return () => clearInterval(interval);
  }, [timerActive]);

  const formatTime = (s) => {
    const mins = Math.floor(s / 60);
    const secs = s % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  if (view === 'SETUP') {
    return (
      <div className="min-h-screen bg-white font-sans text-slate-900 pb-32">
        <header className="px-8 py-6 border-b flex justify-between items-center sticky top-0 bg-white/95 backdrop-blur-sm z-10">
          <div className="flex items-baseline gap-2">
            <h1 className="text-2xl font-black italic text-slate-800 tracking-tighter">看拼音写词 <span className="text-blue-600">V17</span></h1>
          </div>
          <div className="flex items-center gap-6">
            <div className="flex bg-slate-100 p-1 rounded-xl shadow-inner">
              <button onClick={() => setSessionMode('PRACTICE')} className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${sessionMode === 'PRACTICE' ? 'bg-white shadow-sm text-blue-600' : 'text-slate-400'}`}>自由练习</button>
              <button onClick={() => setSessionMode('SELF_TEST')} className={`px-4 py-1.5 rounded-lg text-xs font-bold transition-all ${sessionMode === 'SELF_TEST' ? 'bg-white shadow-sm text-blue-600' : 'text-slate-400'}`}>模拟自测</button>
            </div>
            <div className="flex items-center gap-2 border-l pl-6 border-slate-200">
              <span className="text-xs font-bold text-slate-500 italic uppercase tracking-tighter">仅练错题</span>
              <button onClick={() => setOnlyWrong(!onlyWrong)} className={`w-10 h-6 rounded-full transition-colors relative ${onlyWrong ? 'bg-orange-500' : 'bg-slate-200'}`}>
                <div className={`absolute top-1 w-4 h-4 bg-white rounded-full transition-all ${onlyWrong ? 'left-5' : 'left-1'}`} />
              </button>
            </div>
            <select value={selectedGrade} onChange={(e) => setSelectedGrade(e.target.value)} className="text-sm font-black text-blue-600 border-none bg-transparent outline-none">
              {Object.keys(MOCK_DATA_POOL).map(g => <option key={g} value={g}>{g}</option>)}
            </select>
          </div>
        </header>

        <div className="max-w-6xl mx-auto px-8 py-8">
          <div className="bg-white border border-slate-200 rounded-xl overflow-hidden shadow-sm">
            {Object.entries(units).map(([uName, uWords]) => (
              <div key={uName} className="flex items-start p-4 border-b last:border-0 hover:bg-slate-50 transition-colors group">
                <div className="pt-0.5">
                  <input type="checkbox" checked={selectedUnitNames.has(uName)} onChange={() => {
                    const next = new Set(selectedUnitNames);
                    if (next.has(uName)) next.delete(uName); else next.add(uName);
                    setSelectedUnits(next);
                  }} className="w-4 h-4 rounded border-slate-300 text-blue-600 cursor-pointer" />
                </div>
                <div className="w-28 shrink-0 px-4"><span className="text-sm font-bold text-slate-700">{uName}</span></div>
                <div className="flex-1 text-sm text-slate-400 font-medium leading-relaxed">
                  {uWords.map(w => w.word).join('、')}
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="fixed bottom-0 left-0 right-0 p-8 bg-gradient-to-t from-white to-transparent flex justify-center pointer-events-none">
          <button onClick={startSession} className="pointer-events-auto bg-slate-900 text-white px-16 py-5 rounded-[2rem] font-black text-xl shadow-2xl active:scale-95 transition-all flex items-center gap-4 disabled:opacity-30" disabled={selectedUnitNames.size === 0}>
            开始书写 ({Array.from(selectedUnitNames).reduce((acc, curr) => acc + (units[curr]?.length || 0), 0)} 词) <ArrowRight size={24} />
          </button>
        </div>
      </div>
    );
  }

  // --- PRACTICE / TEST VIEW ---
  return (
    <div className="flex flex-col h-screen overflow-hidden bg-slate-100 font-sans text-slate-900">
      <header className="bg-white border-b px-8 py-4 flex justify-between items-center z-50 shrink-0 shadow-sm">
        <div className="flex items-center gap-6">
          <button onClick={() => setView('SETUP')} className="text-slate-300 hover:text-slate-900 transition-colors">
            <Settings size={22} />
          </button>
          <div className="h-8 w-[1.5px] bg-slate-100" />
          <div className={`px-4 py-1.5 rounded-lg text-xs font-black text-white uppercase tracking-wider ${
            view === 'FINAL' ? 'bg-green-600' : view === 'CORRECTION' ? 'bg-orange-500' : (sessionMode === 'PRACTICE' ? 'bg-blue-600' : 'bg-slate-800')
          }`}>
            {view === 'PRACTICE' ? (sessionMode === 'PRACTICE' ? '自由练习中' : '全屏模拟自测') : 
             view === 'CORRECTION' ? '校对状态 · 孩子标记' : '家长终测 · 掌握度'}
          </div>
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-3 font-mono text-2xl font-black bg-slate-50 px-5 py-1.5 rounded-xl border border-slate-200">
            <Timer size={20} className="text-blue-500" />
            {formatTime(time)}
          </div>
          <button 
            onClick={() => {
              if (view === 'PRACTICE') setView('CORRECTION');
              else if (view === 'CORRECTION') setView('FINAL');
              else {
                const newMastery = { ...mastery };
                currentSessionWords.forEach(w => {
                  if (w.parentMark !== 'white') {
                    newMastery[w.id] = { status: w.parentMark, time };
                  }
                });
                setMastery(newMastery);
                localStorage.setItem('mastery_v1', JSON.stringify(newMastery));
                setTimerActive(false); setView('SETUP');
              }
            }}
            className={`${view === 'FINAL' ? 'bg-green-600' : view === 'CORRECTION' ? 'bg-blue-600' : 'bg-slate-900'} text-white px-8 py-2.5 rounded-xl font-bold shadow-lg active:scale-95 transition-all`}
          >
            {view === 'PRACTICE' ? '完成练习' : view === 'CORRECTION' ? '叫家长批改' : '结束存档并退出'}
          </button>
        </div>
      </header>

      <main className="flex-1 overflow-y-auto p-8 lg:p-12">
        <div className="a4-canvas p-16 lg:p-24 rounded-sm relative min-h-screen">
          {currentSessionWords.length > 0 ? (
            <div className="grid grid-cols-2 lg:grid-cols-3 gap-x-12 gap-y-24">
              {currentSessionWords.map((item, index) => (
                <div key={item.id} className="flex flex-col gap-5">
                  {/* 拼音与交互 */}
                  <div 
                    className="relative select-none"
                    onPointerDown={() => {
                      if (view === 'PRACTICE' && sessionMode === 'PRACTICE') {
                        setHintWordId(item.id);
                        setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: 'red' } : w));
                      }
                    }}
                    onPointerUp={() => setHintWordId(null)}
                    onPointerLeave={() => setHintWordId(null)}
                  >
                    <div className="flex flex-col">
                      <span className="text-3xl font-bold font-kaiti tracking-widest text-slate-800 leading-none">
                        {item.pinyin}
                      </span>
                      {(view === 'CORRECTION' || view === 'FINAL') && (
                        <span className="text-xl font-kaiti text-blue-600 font-bold mt-3 animate-in fade-in duration-500">
                          {item.word}
                        </span>
                      )}
                    </div>

                    {hintWordId === item.id && (
                      <div className="absolute -top-20 left-0 bg-blue-600 text-white text-5xl px-8 py-4 rounded-[2rem] shadow-2xl z-50 font-kaiti font-black ring-8 ring-white popover-up">
                        {item.word}
                        <div className="absolute -bottom-3 left-10 w-6 h-6 bg-blue-600 rotate-45"></div>
                      </div>
                    )}
                  </div>

                  {/* 方格组 */}
                  <div className="flex items-center gap-4">
                    <div className="flex items-center">
                      <div className="tian-ge border-r-0 bg-slate-50 text-slate-900 text-[10px] font-mono font-bold flex items-center justify-center italic">
                        {index + 1}
                      </div>
                      <button 
                        onClick={() => setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, childMark: w.childMark === 'red' ? 'white' : 'red' } : w))}
                        className={`tian-ge border transition-all ${item.childMark === 'red' ? 'bg-red-500 border-red-600 shadow-inner' : 'bg-white border-black'}`}
                      />
                    </div>

                    {view === 'FINAL' && (
                      <div className="flex items-center animate-in slide-in-from-left-4 duration-300">
                        <div className="w-4 h-[1.5px] bg-slate-300 mr-2" />
                        <button 
                          onClick={() => {
                            const next = { white: 'red', red: 'green', green: 'white' };
                            setCurrentSessionWords(prev => prev.map(w => w.id === item.id ? { ...w, parentMark: next[w.parentMark] } : w));
                          }}
                          className={`tian-ge border-2 flex items-center justify-center ${
                            item.parentMark === 'green' ? 'bg-green-500 border-green-600' :
                            item.parentMark === 'red' ? 'bg-red-500 border-red-600' :
                            'bg-white border-slate-400 border-dashed'
                          }`}
                        >
                          {item.parentMark === 'green' && <Check size={18} color="white" strokeWidth={4}/>}
                          {item.parentMark === 'red' && <X size={18} color="white" strokeWidth={4}/>}
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center h-full pt-40">
              <HelpCircle size={64} className="text-slate-200 mb-4 animate-bounce" />
              <p className="text-slate-400 font-bold">没有加载到题目，请返回重试</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}
